mapper.update:
UPDATE M01_COMMON SET
  VALUE = #{vALUE} 
WHERE
  CODE1 = #{cODE1} AND
  CODE2 = #{cODE2} AND
  CODE3 = #{cODE3}

UPDATE M01_COMMON
VALUE = #{vALUE},
WHERE CODE1 = #{cODE1} AND CODE2 = #{cODE2} AND CODE3 = #{cODE3}

mapper.select:
SELECT
  CODE1 AS "cODE1" ,
  CODE2 AS "cODE2" ,
  CODE3 AS "cODE3" ,
  VALUE AS "vALUE" 
FROM M01_COMMON

  AND CODE1 = #{cODE1}
  AND CODE2 = #{cODE2}
  AND CODE3 = #{cODE3}
  AND VALUE = #{vALUE}

ORDER BY CODE1, CODE2, CODE3
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

