mapper.update:
UPDATE T03_GEN_MEMBER
GEN_ID = #{gENID}, PER_ID = #{pERID}, FAR_ID = #{fARID}, MOT_ID = #{mOTID},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  GEN_ID AS "gENID" ,
  PER_ID AS "pERID" ,
  FAR_ID AS "fARID" ,
  MOT_ID AS "mOTID" 
FROM T03_GEN_MEMBER

  AND ID = #{iD}
  AND GEN_ID = #{gENID}
  AND PER_ID = #{pERID}
  AND FAR_ID = #{fARID}
  AND MOT_ID = #{mOTID}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

