mapper.update:
UPDATE T06_PER_HISTORY
PER_ID = #{pERID}, EVENT_TYPE = #{eVENTTYPE}, EVENT_DATE = #{eVENTDATE}, EVENT_TIME = #{eVENTTIME}, LATITUDE = #{lATITUDE}, LONGITUDE = #{lONGITUDE}, TITLE = #{tITLE}, COVERIMAGE = #{cOVERIMAGE}, PRAISE = #{pRAISE}, ISPUBLIC = #{iSPUBLIC}, REMARKS = #{rEMARKS}, CREATE_DATE = #{cREATEDATE}, CREATE_TIME = #{cREATETIME},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  PER_ID AS "pERID" ,
  EVENT_TYPE AS "eVENTTYPE" ,
  EVENT_DATE AS "eVENTDATE" ,
  EVENT_TIME AS "eVENTTIME" ,
  LATITUDE AS "lATITUDE" ,
  LONGITUDE AS "lONGITUDE" ,
  TITLE AS "tITLE" ,
  COVERIMAGE AS "cOVERIMAGE" ,
  PRAISE AS "pRAISE" ,
  ISPUBLIC AS "iSPUBLIC" ,
  REMARKS AS "rEMARKS" ,
  CREATE_DATE AS "cREATEDATE" ,
  CREATE_TIME AS "cREATETIME" 
FROM T06_PER_HISTORY

  AND ID = #{iD}
  AND PER_ID = #{pERID}
  AND EVENT_TYPE = #{eVENTTYPE}
  AND EVENT_DATE = #{eVENTDATE}
  AND EVENT_TIME = #{eVENTTIME}
  AND LATITUDE = #{lATITUDE}
  AND LONGITUDE = #{lONGITUDE}
  AND TITLE = #{tITLE}
  AND COVERIMAGE = #{cOVERIMAGE}
  AND PRAISE = #{pRAISE}
  AND ISPUBLIC = #{iSPUBLIC}
  AND REMARKS = #{rEMARKS}
  AND CREATE_DATE = #{cREATEDATE}
  AND CREATE_TIME = #{cREATETIME}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

