SELECT ID AS "iD" , PER_ID AS "pERID" , EVENT_TYPE AS "eVENTTYPE" , EVENT_DATE AS "eVENTDATE" , EVENT_TIME AS "eVENTTIME" , LATITUDE AS "lATITUDE" , LONGITUDE AS "lONGITUDE" , TITLE AS "tITLE" , COVERIMAGE AS "cOVERIMAGE" , PRAISE AS "pRAISE" , ISPUBLIC AS "iSPUBLIC" , REMARKS AS "rEMARKS" , CREATE_DATE AS "cREATEDATE" , CREATE_TIME AS "cREATETIME" FROM T06_PER_HISTORY
AND ID LIKE '%'|| #{iD} ||'%' AND PER_ID LIKE '%'|| #{pERID} ||'%' AND EVENT_TYPE LIKE '%'|| #{eVENTTYPE} ||'%' AND EVENT_DATE LIKE '%'|| #{eVENTDATE} ||'%' AND EVENT_TIME LIKE '%'|| #{eVENTTIME} ||'%' AND LATITUDE LIKE '%'|| #{lATITUDE} ||'%' AND LONGITUDE LIKE '%'|| #{lONGITUDE} ||'%' AND TITLE LIKE '%'|| #{tITLE} ||'%' AND COVERIMAGE LIKE '%'|| #{cOVERIMAGE} ||'%' AND PRAISE LIKE '%'|| #{pRAISE} ||'%' AND ISPUBLIC LIKE '%'|| #{iSPUBLIC} ||'%' AND REMARKS LIKE '%'|| #{rEMARKS} ||'%' AND CREATE_DATE LIKE '%'|| #{cREATEDATE} ||'%' AND CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

