mapper.update:
UPDATE T07_PER_SET
PER_ID = #{pERID}, SHOW_TYPE = #{sHOWTYPE}, CONTACT_SHOW = #{cONTACTSHOW}, VOICE_TYPE = #{vOICETYPE},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  PER_ID AS "pERID" ,
  SHOW_TYPE AS "sHOWTYPE" ,
  CONTACT_SHOW AS "cONTACTSHOW" ,
  VOICE_TYPE AS "vOICETYPE" 
FROM T07_PER_SET

  AND ID = #{iD}
  AND PER_ID = #{pERID}
  AND SHOW_TYPE = #{sHOWTYPE}
  AND CONTACT_SHOW = #{cONTACTSHOW}
  AND VOICE_TYPE = #{vOICETYPE}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

