AND ID LIKE '%'|| #{iD} ||'%' AND PER_ID LIKE '%'|| #{pERID} ||'%' AND SHOW_TYPE LIKE '%'|| #{sHOWTYPE} ||'%' AND CONTACT_SHOW LIKE '%'|| #{cONTACTSHOW} ||'%' AND VOICE_TYPE LIKE '%'|| #{vOICETYPE} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

