mapper.update:
UPDATE T09_PER_HIS_STREAM
HIS_ID = #{hisID}, TYPE = #{type}, PATH = #{path}, REMARKS = #{remarks}, CREATE_DATE = #{createDate}, CREATE_TIME = #{createTime},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  HIS_ID AS "hisID" ,
  TYPE AS "type" ,
  PATH AS "path" ,
  REMARKS AS "remarks" ,
  CREATE_DATE AS "createDate" ,
  CREATE_TIME AS "createTime" 
FROM T09_PER_HIS_STREAM

  AND ID = #{iD}
  AND HIS_ID = #{hisID}
  AND TYPE = #{type}
  AND PATH = #{path}
  AND REMARKS = #{remarks}
  AND CREATE_DATE = #{createDate}
  AND CREATE_TIME = #{createTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

