SELECT ID AS "iD" , HIS_ID AS "hisID" , TYPE AS "type" , PATH AS "path" , REMARKS AS "remarks" , CREATE_DATE AS "createDate" , CREATE_TIME AS "createTime" FROM T09_PER_HIS_STREAM
AND ID LIKE '%'|| #{iD} ||'%' AND HIS_ID LIKE '%'|| #{hisID} ||'%' AND TYPE LIKE '%'|| #{type} ||'%' AND PATH LIKE '%'|| #{path} ||'%' AND REMARKS LIKE '%'|| #{remarks} ||'%' AND CREATE_DATE LIKE '%'|| #{createDate} ||'%' AND CREATE_TIME LIKE '%'|| #{createTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

