mapper.update:
UPDATE T10_PER_STREAM
PER_ID = #{perID}, TYPE = #{type}, PATH = #{path}, REMARKS = #{remarks},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  PER_ID AS "perID" ,
  TYPE AS "type" ,
  PATH AS "path" ,
  REMARKS AS "remarks" 
FROM T10_PER_STREAM

  AND ID = #{iD}
  AND PER_ID = #{perID}
  AND TYPE = #{type}
  AND PATH = #{path}
  AND REMARKS = #{remarks}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

