SELECT ID AS "iD" , PER_ID AS "perID" , TYPE AS "type" , PATH AS "path" , REMARKS AS "remarks" FROM T10_PER_STREAM
AND ID LIKE '%'|| #{iD} ||'%' AND PER_ID LIKE '%'|| #{perID} ||'%' AND TYPE LIKE '%'|| #{type} ||'%' AND PATH LIKE '%'|| #{path} ||'%' AND REMARKS LIKE '%'|| #{remarks} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

