ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

SELECT
  ID AS "iD" ,
  PER_ID AS "perID" ,
  TYPE AS "type" ,
  PATH AS "path" ,
  REMARKS AS "remarks" 
FROM T10_PER_STREAM
WHERE ${_dynamicWhereCondition}
ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

