mapper.update:
UPDATE T13_NOTICE
NOTICE = #{nOTICE}, NOTICE_DATE = #{nOTICEDATE}, NOTICE_TIME = #{nOTICETIME}, LATITUDE = #{lATITUDE}, LONGITUDE = #{lONGITUDE}, CREATE_DATE = #{cREATEDATE}, CREATE_TIME = #{cREATETIME}, CREATE_PER = #{cREATEPER},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  NOTICE AS "nOTICE" ,
  NOTICE_DATE AS "nOTICEDATE" ,
  NOTICE_TIME AS "nOTICETIME" ,
  LATITUDE AS "lATITUDE" ,
  LONGITUDE AS "lONGITUDE" ,
  CREATE_DATE AS "cREATEDATE" ,
  CREATE_TIME AS "cREATETIME" ,
  CREATE_PER AS "cREATEPER" 
FROM T13_NOTICE

  AND ID = #{iD}
  AND NOTICE = #{nOTICE}
  AND NOTICE_DATE = #{nOTICEDATE}
  AND NOTICE_TIME = #{nOTICETIME}
  AND LATITUDE = #{lATITUDE}
  AND LONGITUDE = #{lONGITUDE}
  AND CREATE_DATE = #{cREATEDATE}
  AND CREATE_TIME = #{cREATETIME}
  AND CREATE_PER = #{cREATEPER}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

