SELECT ID AS "iD" , NOTICE AS "nOTICE" , NOTICE_DATE AS "nOTICEDATE" , NOTICE_TIME AS "nOTICETIME" , LATITUDE AS "lATITUDE" , LONGITUDE AS "lONGITUDE" , CREATE_DATE AS "cREATEDATE" , CREATE_TIME AS "cREATETIME" , CREATE_PER AS "cREATEPER" FROM T13_NOTICE
AND ID LIKE '%'|| #{iD} ||'%' AND NOTICE LIKE '%'|| #{nOTICE} ||'%' AND NOTICE_DATE LIKE '%'|| #{nOTICEDATE} ||'%' AND NOTICE_TIME LIKE '%'|| #{nOTICETIME} ||'%' AND LATITUDE LIKE '%'|| #{lATITUDE} ||'%' AND LONGITUDE LIKE '%'|| #{lONGITUDE} ||'%' AND CREATE_DATE LIKE '%'|| #{cREATEDATE} ||'%' AND CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%' AND CREATE_PER LIKE '%'|| #{cREATEPER} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

