SELECT ID AS "iD" , NOTICE AS "notice" , NOTICE_DATE AS "noticeDate" , NOTICE_TIME AS "noticeTime" , LATITUDE AS "latitude" , LONGITUDE AS "longitude" , CREATE_PER AS "createPer" , CREATE_DATE AS "createDate" , CREATE_TIME AS "createTime" FROM T13_NOTICE
AND ID LIKE '%'|| #{iD} ||'%' AND NOTICE LIKE '%'|| #{notice} ||'%' AND NOTICE_DATE LIKE '%'|| #{noticeDate} ||'%' AND NOTICE_TIME LIKE '%'|| #{noticeTime} ||'%' AND LATITUDE LIKE '%'|| #{latitude} ||'%' AND LONGITUDE LIKE '%'|| #{longitude} ||'%' AND CREATE_PER LIKE '%'|| #{createPer} ||'%' AND CREATE_DATE LIKE '%'|| #{createDate} ||'%' AND CREATE_TIME LIKE '%'|| #{createTime} ||'%'
