mapper.update:
UPDATE M02_SYSTEM_VERSION
IMSI = #{iMSI}, CHANNEL = #{cHANNEL}, VERSION = #{vERSION}, LOAD_PATH = #{lOADPATH}, DESCRIP = #{dESCRIP}, DEL_FLG = #{dELFLG},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  IMSI AS "iMSI" ,
  CHANNEL AS "cHANNEL" ,
  VERSION AS "vERSION" ,
  LOAD_PATH AS "lOADPATH" ,
  DESCRIP AS "dESCRIP" ,
  DEL_FLG AS "dELFLG" 
FROM M02_SYSTEM_VERSION

  AND ID = #{iD}
  AND IMSI = #{iMSI}
  AND CHANNEL = #{cHANNEL}
  AND VERSION = #{vERSION}
  AND LOAD_PATH = #{lOADPATH}
  AND DESCRIP = #{dESCRIP}
  AND DEL_FLG = #{dELFLG}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

SELECT
