SELECT ID AS "iD" , IMSI AS "iMSI" , CHANNEL AS "cHANNEL" , VERSION AS "vERSION" , LOAD_PATH AS "lOADPATH" , DESCRIP AS "dESCRIP" , DEL_FLG AS "dELFLG" FROM M02_SYSTEM_VERSION
AND ID LIKE '%'|| #{iD} ||'%' AND IMSI LIKE '%'|| #{iMSI} ||'%' AND CHANNEL LIKE '%'|| #{cHANNEL} ||'%' AND VERSION LIKE '%'|| #{vERSION} ||'%' AND LOAD_PATH LIKE '%'|| #{lOADPATH} ||'%' AND DESCRIP LIKE '%'|| #{dESCRIP} ||'%' AND DEL_FLG LIKE '%'|| #{dELFLG} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

SELECT ID AS "iD" , IMSI AS "iMSI" , CHANNEL AS "cHANNEL" , VERSION AS "vERSION" , LOAD_PATH AS "lOADPATH" , DESCRIP AS "dESCRIP" , DEL_FLG AS "dELFLG" FROM M02_SYSTEM_VERSION
OR ID LIKE '%'|| #{iD} ||'%' OR IMSI LIKE '%'|| #{iMSI} ||'%' OR CHANNEL LIKE '%'|| #{cHANNEL} ||'%' OR VERSION LIKE '%'|| #{vERSION} ||'%' OR LOAD_PATH LIKE '%'|| #{lOADPATH} ||'%' OR DESCRIP LIKE '%'|| #{dESCRIP} ||'%' OR DEL_FLG LIKE '%'|| #{dELFLG} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

