mapper.update:
UPDATE T01_GENEALOGY
FIRST_NAME = #{fIRSTNAME}, ARMS = #{aRMS}, VIDEO = #{vIDEO}, INFOMATION = #{iNFOMATION}, BIGINT = #{bIGINT},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  FIRST_NAME AS "fIRSTNAME" ,
  ARMS AS "aRMS" ,
  VIDEO AS "vIDEO" ,
  INFOMATION AS "iNFOMATION" ,
  BIGINT AS "bIGINT" 
FROM T01_GENEALOGY

  AND ID = #{iD}
  AND FIRST_NAME = #{fIRSTNAME}
  AND ARMS = #{aRMS}
  AND VIDEO = #{vIDEO}
  AND INFOMATION = #{iNFOMATION}
  AND BIGINT = #{bIGINT}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

