SELECT ID AS "iD" , FIRST_NAME AS "fIRSTNAME" , ARMS AS "aRMS" , VIDEO AS "vIDEO" , INFOMATION AS "iNFOMATION" , BIGINT AS "bIGINT" FROM T01_GENEALOGY
AND ID LIKE '%'|| #{iD} ||'%' AND FIRST_NAME LIKE '%'|| #{fIRSTNAME} ||'%' AND ARMS LIKE '%'|| #{aRMS} ||'%' AND VIDEO LIKE '%'|| #{vIDEO} ||'%' AND INFOMATION LIKE '%'|| #{iNFOMATION} ||'%' AND BIGINT LIKE '%'|| #{bIGINT} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

