SELECT ID AS "iD" , FIRST_NAME AS "firstName" , ARMS AS "arms" , VIDEO AS "video" , INFOMATION AS "infomation" , OWNER_ID AS "ownerID" , CREATE_DATE AS "createDate" , CREATE_TIME AS "createTime" FROM T01_GENEALOGY
AND ID LIKE '%'|| #{iD} ||'%' AND FIRST_NAME LIKE '%'|| #{firstName} ||'%' AND ARMS LIKE '%'|| #{arms} ||'%' AND VIDEO LIKE '%'|| #{video} ||'%' AND INFOMATION LIKE '%'|| #{infomation} ||'%' AND OWNER_ID LIKE '%'|| #{ownerID} ||'%' AND CREATE_DATE LIKE '%'|| #{createDate} ||'%' AND CREATE_TIME LIKE '%'|| #{createTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

