mapper.update:
UPDATE T02_PERSONAL
FIRST_NAME = #{firstName}, LAST_NAME = #{lastName}, NO = #{nO}, MOBILE = #{mobile}, MAILE = #{maile}, BIRTH_DATE = #{birthDate}, BIRTH_TIME = #{birthTime}, SEX = #{sex}, COVER = #{cover}, HEAD_PORTRAIT = #{headPortrait}, VIDEO = #{video}, INFOMATION = #{infomation}, LATITUDE = #{latitude}, LONGITUDE = #{longitude}, REGEDIT_DATE = #{regeditDate}, REGEDIT_TIME = #{regeditTime}, OWNER_ID = #{ownerID}, CREATE_DATE = #{createDate}, CREATE_TIME = #{createTime},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  FIRST_NAME AS "firstName" ,
  LAST_NAME AS "lastName" ,
  NO AS "nO" ,
  MOBILE AS "mobile" ,
  MAILE AS "maile" ,
  BIRTH_DATE AS "birthDate" ,
  BIRTH_TIME AS "birthTime" ,
  SEX AS "sex" ,
  COVER AS "cover" ,
  HEAD_PORTRAIT AS "headPortrait" ,
  VIDEO AS "video" ,
  INFOMATION AS "infomation" ,
  LATITUDE AS "latitude" ,
  LONGITUDE AS "longitude" ,
  REGEDIT_DATE AS "regeditDate" ,
  REGEDIT_TIME AS "regeditTime" ,
  OWNER_ID AS "ownerID" ,
  CREATE_DATE AS "createDate" ,
  CREATE_TIME AS "createTime" 
FROM T02_PERSONAL

  AND ID = #{iD}
  AND FIRST_NAME = #{firstName}
  AND LAST_NAME = #{lastName}
  AND NO = #{nO}
  AND MOBILE = #{mobile}
  AND MAILE = #{maile}
  AND BIRTH_DATE = #{birthDate}
  AND BIRTH_TIME = #{birthTime}
  AND SEX = #{sex}
  AND COVER = #{cover}
  AND HEAD_PORTRAIT = #{headPortrait}
  AND VIDEO = #{video}
  AND INFOMATION = #{infomation}
  AND LATITUDE = #{latitude}
  AND LONGITUDE = #{longitude}
  AND REGEDIT_DATE = #{regeditDate}
  AND REGEDIT_TIME = #{regeditTime}
  AND OWNER_ID = #{ownerID}
  AND CREATE_DATE = #{createDate}
  AND CREATE_TIME = #{createTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

