SELECT ID AS "iD" , FIRST_NAME AS "firstName" , LAST_NAME AS "lastName" , NO AS "nO" , MOBILE AS "mobile" , MAILE AS "maile" , BIRTH_DATE AS "birthDate" , BIRTH_TIME AS "birthTime" , SEX AS "sex" , COVER AS "cover" , HEAD_PORTRAIT AS "headPortrait" , VIDEO AS "video" , INFOMATION AS "infomation" , LATITUDE AS "latitude" , LONGITUDE AS "longitude" , REGEDIT_DATE AS "regeditDate" , REGEDIT_TIME AS "regeditTime" , OWNER_ID AS "ownerID" , CREATE_DATE AS "createDate" , CREATE_TIME AS "createTime" FROM T02_PERSONAL
AND ID LIKE '%'|| #{iD} ||'%' AND FIRST_NAME LIKE '%'|| #{firstName} ||'%' AND LAST_NAME LIKE '%'|| #{lastName} ||'%' AND NO LIKE '%'|| #{nO} ||'%' AND MOBILE LIKE '%'|| #{mobile} ||'%' AND MAILE LIKE '%'|| #{maile} ||'%' AND BIRTH_DATE LIKE '%'|| #{birthDate} ||'%' AND BIRTH_TIME LIKE '%'|| #{birthTime} ||'%' AND SEX LIKE '%'|| #{sex} ||'%' AND COVER LIKE '%'|| #{cover} ||'%' AND HEAD_PORTRAIT LIKE '%'|| #{headPortrait} ||'%' AND VIDEO LIKE '%'|| #{video} ||'%' AND INFOMATION LIKE '%'|| #{infomation} ||'%' AND LATITUDE LIKE '%'|| #{latitude} ||'%' AND LONGITUDE LIKE '%'|| #{longitude} ||'%' AND REGEDIT_DATE LIKE '%'|| #{regeditDate} ||'%' AND REGEDIT_TIME LIKE '%'|| #{regeditTime} ||'%' AND OWNER_ID LIKE '%'|| #{ownerID} ||'%' AND CREATE_DATE LIKE '%'|| #{createDate} ||'%' AND CREATE_TIME LIKE '%'|| #{createTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

