mapper.update:
UPDATE T03_GEN_MEMBER
GEN_ID = #{genID}, PER_ID = #{perID}, FAR_ID = #{farID}, MOT_ID = #{motID}, ONWER_ID = #{onwerID}, CREATE_DATE = #{createDate}, CREATE_TIME = #{createTime},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  GEN_ID AS "genID" ,
  PER_ID AS "perID" ,
  FAR_ID AS "farID" ,
  MOT_ID AS "motID" ,
  ONWER_ID AS "onwerID" ,
  CREATE_DATE AS "createDate" ,
  CREATE_TIME AS "createTime" 
FROM T03_GEN_MEMBER

  AND ID = #{iD}
  AND GEN_ID = #{genID}
  AND PER_ID = #{perID}
  AND FAR_ID = #{farID}
  AND MOT_ID = #{motID}
  AND ONWER_ID = #{onwerID}
  AND CREATE_DATE = #{createDate}
  AND CREATE_TIME = #{createTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

