mapper.update:
UPDATE T06_PER_HISTORY
PER_ID = #{perID}, EVENT_TYPE = #{eventType}, EVENT_DATE = #{eventDate}, EVENT_TIME = #{eventTime}, LATITUDE = #{latitude}, LONGITUDE = #{longitude}, TITLE = #{title}, COVER_IMAGE = #{coverImage}, PRAISE = #{praise}, IS_PUBLIC = #{isPublic}, REMARKS = #{remarks}, CREATE_DATE = #{createDate}, CREATE_TIME = #{createTime},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  PER_ID AS "perID" ,
  EVENT_TYPE AS "eventType" ,
  EVENT_DATE AS "eventDate" ,
  EVENT_TIME AS "eventTime" ,
  LATITUDE AS "latitude" ,
  LONGITUDE AS "longitude" ,
  TITLE AS "title" ,
  COVER_IMAGE AS "coverImage" ,
  PRAISE AS "praise" ,
  IS_PUBLIC AS "isPublic" ,
  REMARKS AS "remarks" ,
  CREATE_DATE AS "createDate" ,
  CREATE_TIME AS "createTime" 
FROM T06_PER_HISTORY

  AND ID = #{iD}
  AND PER_ID = #{perID}
  AND EVENT_TYPE = #{eventType}
  AND EVENT_DATE = #{eventDate}
  AND EVENT_TIME = #{eventTime}
  AND LATITUDE = #{latitude}
  AND LONGITUDE = #{longitude}
  AND TITLE = #{title}
  AND COVER_IMAGE = #{coverImage}
  AND PRAISE = #{praise}
  AND IS_PUBLIC = #{isPublic}
  AND REMARKS = #{remarks}
  AND CREATE_DATE = #{createDate}
  AND CREATE_TIME = #{createTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

