SELECT ID AS "iD" , PER_ID AS "perID" , EVENT_TYPE AS "eventType" , EVENT_DATE AS "eventDate" , EVENT_TIME AS "eventTime" , LATITUDE AS "latitude" , LONGITUDE AS "longitude" , TITLE AS "title" , COVER_IMAGE AS "coverImage" , PRAISE AS "praise" , IS_PUBLIC AS "isPublic" , REMARKS AS "remarks" , CREATE_DATE AS "createDate" , CREATE_TIME AS "createTime" FROM T06_PER_HISTORY
AND ID LIKE '%'|| #{iD} ||'%' AND PER_ID LIKE '%'|| #{perID} ||'%' AND EVENT_TYPE LIKE '%'|| #{eventType} ||'%' AND EVENT_DATE LIKE '%'|| #{eventDate} ||'%' AND EVENT_TIME LIKE '%'|| #{eventTime} ||'%' AND LATITUDE LIKE '%'|| #{latitude} ||'%' AND LONGITUDE LIKE '%'|| #{longitude} ||'%' AND TITLE LIKE '%'|| #{title} ||'%' AND COVER_IMAGE LIKE '%'|| #{coverImage} ||'%' AND PRAISE LIKE '%'|| #{praise} ||'%' AND IS_PUBLIC LIKE '%'|| #{isPublic} ||'%' AND REMARKS LIKE '%'|| #{remarks} ||'%' AND CREATE_DATE LIKE '%'|| #{createDate} ||'%' AND CREATE_TIME LIKE '%'|| #{createTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

