mapper.update:
UPDATE T09_PER_HIS_STREAM
HIS_ID = #{hISID}, TYPE = #{tYPE}, PATH = #{pATH}, REMARKS = #{rEMARKS}, CREATE_TIME = #{cREATETIME},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  HIS_ID AS "hISID" ,
  TYPE AS "tYPE" ,
  PATH AS "pATH" ,
  REMARKS AS "rEMARKS" ,
  CREATE_TIME AS "cREATETIME" 
FROM T09_PER_HIS_STREAM

  AND ID = #{iD}
  AND HIS_ID = #{hISID}
  AND TYPE = #{tYPE}
  AND PATH = #{pATH}
  AND REMARKS = #{rEMARKS}
  AND CREATE_TIME = #{cREATETIME}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

