SELECT ID AS "iD" , HIS_ID AS "hISID" , TYPE AS "tYPE" , PATH AS "pATH" , REMARKS AS "rEMARKS" , CREATE_TIME AS "cREATETIME" FROM T09_PER_HIS_STREAM
AND ID LIKE '%'|| #{iD} ||'%' AND HIS_ID LIKE '%'|| #{hISID} ||'%' AND TYPE LIKE '%'|| #{tYPE} ||'%' AND PATH LIKE '%'|| #{pATH} ||'%' AND REMARKS LIKE '%'|| #{rEMARKS} ||'%' AND CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

