mapper.update:
UPDATE T16_NOTICE_COMMENT
NOT_ID = #{nOTID}, PER_ID = #{pERID}, COMMENT = #{cOMMENT}, CREATE_DATE = #{cREATEDATE}, CREATE_TIME = #{cREATETIME},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  NOT_ID AS "nOTID" ,
  PER_ID AS "pERID" ,
  COMMENT AS "cOMMENT" ,
  CREATE_DATE AS "cREATEDATE" ,
  CREATE_TIME AS "cREATETIME" 
FROM T16_NOTICE_COMMENT

  AND ID = #{iD}
  AND NOT_ID = #{nOTID}
  AND PER_ID = #{pERID}
  AND COMMENT = #{cOMMENT}
  AND CREATE_DATE = #{cREATEDATE}
  AND CREATE_TIME = #{cREATETIME}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

