SELECT ID AS "iD" , NOT_ID AS "nOTID" , PER_ID AS "pERID" , COMMENT AS "cOMMENT" , CREATE_DATE AS "cREATEDATE" , CREATE_TIME AS "cREATETIME" FROM T16_NOTICE_COMMENT
AND ID LIKE '%'|| #{iD} ||'%' AND NOT_ID LIKE '%'|| #{nOTID} ||'%' AND PER_ID LIKE '%'|| #{pERID} ||'%' AND COMMENT LIKE '%'|| #{cOMMENT} ||'%' AND CREATE_DATE LIKE '%'|| #{cREATEDATE} ||'%' AND CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

