OR ID LIKE '%'|| #{iD} ||'%' OR NOT_ID LIKE '%'|| #{nOTID} ||'%' OR PER_ID LIKE '%'|| #{pERID} ||'%' OR COMMENT LIKE '%'|| #{cOMMENT} ||'%' OR CREATE_DATE LIKE '%'|| #{cREATEDATE} ||'%' OR CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

