mapper.update:
UPDATE T17_PER_QUESTION
PER_ID = #{pERID}, QUESTION = #{qUESTION}, STATE = #{sTATE}, CREATE_DATE = #{cREATEDATE}, CREATE_TIME = #{cREATETIME}, UPDATE_DATE = #{uPDATEDATE}, UPDATE_TIME = #{uPDATETIME},
WHERE ID = #{iD}

mapper.select:
SELECT
  ID AS "iD" ,
  PER_ID AS "pERID" ,
  QUESTION AS "qUESTION" ,
  STATE AS "sTATE" ,
  CREATE_DATE AS "cREATEDATE" ,
  CREATE_TIME AS "cREATETIME" ,
  UPDATE_DATE AS "uPDATEDATE" ,
  UPDATE_TIME AS "uPDATETIME" 
FROM T17_PER_QUESTION

  AND ID = #{iD}
  AND PER_ID = #{pERID}
  AND QUESTION = #{qUESTION}
  AND STATE = #{sTATE}
  AND CREATE_DATE = #{cREATEDATE}
  AND CREATE_TIME = #{cREATETIME}
  AND UPDATE_DATE = #{uPDATEDATE}
  AND UPDATE_TIME = #{uPDATETIME}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

