SELECT ID AS "iD" , PER_ID AS "pERID" , QUESTION AS "qUESTION" , STATE AS "sTATE" , CREATE_DATE AS "cREATEDATE" , CREATE_TIME AS "cREATETIME" , UPDATE_DATE AS "uPDATEDATE" , UPDATE_TIME AS "uPDATETIME" FROM T17_PER_QUESTION
AND ID LIKE '%'|| #{iD} ||'%' AND PER_ID LIKE '%'|| #{pERID} ||'%' AND QUESTION LIKE '%'|| #{qUESTION} ||'%' AND STATE LIKE '%'|| #{sTATE} ||'%' AND CREATE_DATE LIKE '%'|| #{cREATEDATE} ||'%' AND CREATE_TIME LIKE '%'|| #{cREATETIME} ||'%' AND UPDATE_DATE LIKE '%'|| #{uPDATEDATE} ||'%' AND UPDATE_TIME LIKE '%'|| #{uPDATETIME} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

