mapper.update:
UPDATE TCOMBLCM
VALUE = #{value}, NOTES = #{notes}, LAST_OP_ID = #{lastOpId}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE APPL_ID = #{applId} AND FUNCTION_ID = #{functionId} AND ITEM_ID = #{itemId}

mapper.select:
SELECT
  APPL_ID AS "applId" ,
  FUNCTION_ID AS "functionId" ,
  ITEM_ID AS "itemId" ,
  VALUE AS "value" ,
  NOTES AS "notes" ,
  LAST_OP_ID AS "lastOpId" ,
  LAST_DATE AS "lastDate" ,
  LAST_TIME AS "lastTime" 
FROM TCOMBLCM

  AND APPL_ID = #{applId}
  AND FUNCTION_ID = #{functionId}
  AND ITEM_ID = #{itemId}
  AND VALUE = #{value}
  AND NOTES = #{notes}
  AND LAST_OP_ID = #{lastOpId}
  AND LAST_DATE = #{lastDate}
  AND LAST_TIME = #{lastTime}

ORDER BY APPL_ID, FUNCTION_ID, ITEM_ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

