SELECT APPL_ID AS "applId" , FUNCTION_ID AS "functionId" , ITEM_ID AS "itemId" , VALUE AS "value" , NOTES AS "notes" , LAST_OP_ID AS "lastOpId" , LAST_DATE AS "lastDate" , LAST_TIME AS "lastTime" FROM TCOMBLCM
AND APPL_ID LIKE '%'|| #{applId} ||'%' AND FUNCTION_ID LIKE '%'|| #{functionId} ||'%' AND ITEM_ID LIKE '%'|| #{itemId} ||'%' AND VALUE LIKE '%'|| #{value} ||'%' AND NOTES LIKE '%'|| #{notes} ||'%' AND LAST_OP_ID LIKE '%'|| #{lastOpId} ||'%' AND LAST_DATE LIKE '%'|| #{lastDate} ||'%' AND LAST_TIME LIKE '%'|| #{lastTime} ||'%'
ORDER BY APPL_ID, FUNCTION_ID, ITEM_ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

SELECT APPL_ID AS "applId" , FUNCTION_ID AS "functionId" , ITEM_ID AS "itemId" , VALUE AS "value" , NOTES AS "notes" , LAST_OP_ID AS "lastOpId" , LAST_DATE AS "lastDate" , LAST_TIME AS "lastTime" FROM TCOMBLCM
OR APPL_ID LIKE '%'|| #{applId} ||'%' OR FUNCTION_ID LIKE '%'|| #{functionId} ||'%' OR ITEM_ID LIKE '%'|| #{itemId} ||'%' OR VALUE LIKE '%'|| #{value} ||'%' OR NOTES LIKE '%'|| #{notes} ||'%' OR LAST_OP_ID LIKE '%'|| #{lastOpId} ||'%' OR LAST_DATE LIKE '%'|| #{lastDate} ||'%' OR LAST_TIME LIKE '%'|| #{lastTime} ||'%'
ORDER BY APPL_ID, FUNCTION_ID, ITEM_ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

